    database: "readerweb_0962",
    user: "teste",
    senha: "teste.1234@",
    porta: 3306,
  },
};

// ==== OBRIGAT√ìRIO TER c√≥digo do IManager ====
const codigoIManager = (j.CodigoIManager ?? "0962");
const selected = configuracaoPorCliente[codigoIManager];
if (!selected) {
  throw new Error(
    `Cliente ` + codigoIManager + ` n√£o informado, favor validar para utiliza√ß√£o do m√≥dulo.`
  );
}

j.configuracao = {
    host: selected.host,
    database: selected.database,
    user: selected.user,
    password: selected.senha,
    port: selected.porta,
  };

return item;

// Captura o node "Combine schema data and chat input"
const combineNode = $('Combine schema data and chat input').first();
const pedido = combineNode?.json?.chatinput || '';

// Captura o dataset atual
const src = $json.envelope ?? $json;
const dataIn = Array.isArray(src?.resultado?.data)
  ? src.resultado.data
  : Array.isArray(src?.data)
  ? src.data
  : [];

// Normaliza chaves e converte valores num√©ricos
const normalizeKey = (k) =>
  k
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_')
    .replace(/^_|_$/g, '')
    .toLowerCase();

const toNumber = (v) =>
