// ==============================
// 1. ENTRADAS B√ÅSICAS
// ==============================
const pedido = $json.pedido_cliente;
const data = $json.data || [];
const query = $json.query || "";

// diagnostico pode vir como "diagnostico" ou "Diagn√≥stico"
const d = $json.Diagn√≥stico || $json.diagnostico || {};

// ==============================
// 2. HELPERS GERAIS
// ==============================
function toArraySafe(val) {
  if (val == null) return [];
  if (Array.isArray(val)) return val;
  if (typeof val === "object") return [JSON.stringify(val, null, 2)];
  return [String(val)];
}

function toTextSafe(val, fallback = "‚Äî") {
  if (val == null) return fallback;
  if (typeof val === "string") return val;
  if (typeof val === "number" || typeof val === "boolean") return String(val);
  return JSON.stringify(val, null, 2);
}

// ==============================
// 3. FORMATA√á√ÉO NUM√âRICA POR CONTEXTO
// ==============================
function formatMoneyBr(num) {
  if (num == null || isNaN(num)) return "R$ 0,00";
  return (
    "R$ " +
    Number(num).toLocaleString("pt-BR", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    })
  );
}

function formatPercentBr(num) {
  if (num == null || isNaN(num)) return "0%";
  return (
    Number(num).toLocaleString("pt-BR", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }) + "%"
  );
}

function formatIntegerBr(num) {
  if (num == null || isNaN(num)) return "0";
  return Number(num).toLocaleString("pt-BR", {
    maximumFractionDigits: 0,
  });
}

// decide como formatar de acordo com o nome do campo / contexto pai
function formatNumberWithContext(chave, parentKey, value) {
  const k = (chave || "").toLowerCase();
  const p = (parentKey || "").toLowerCase();

  // 1) VALOR MONET√ÅRIO
  if (
    k.includes("valor") ||
    k.includes("fatura") ||
    k.includes("faturado") ||
    k.includes("receita") ||
    p.includes("valor_faturado") ||
    p.includes("faturado") ||
    p.includes("faturas")
  ) {
    return formatMoneyBr(value);
  }

  // 2) PERCENTUAL
  if (
    k.includes("percent") ||
    k.includes("perc") ||
    k.endsWith("_pct") ||
    k.endsWith("_percentual") ||
    k.includes("taxa") ||
    k.includes("indice") ||
    k.includes("√≠ndice")
  ) {
    return formatPercentBr(value);
  }

  // 3) CONSUMO / VOLUME (m¬≥)
  if (
    k.includes("consumo") ||
    k.includes("volume") ||
    k.includes("m3") ||
    k.includes("m¬≥")
  ) {
    return `${formatIntegerBr(value)} m¬≥`;
  }

  // 4) LEITURAS / QUANTIDADES / REGISTROS (inteiro com milhar)
  if (
    k.includes("leitura") ||
    k.includes("leituras") ||
    k.includes("qtd") ||
    k.includes("quantidade") ||
    k.includes("total") ||
    k.includes("economias") ||
    k.includes("registros")
  ) {
    return formatIntegerBr(value);
  }

  // 5) DEFAULT: n√∫mero "cru", mas em pt-BR
  return Number(value).toLocaleString("pt-BR", {
    maximumFractionDigits: 2,
  });
}

// ==============================
// 4. AMOSTRA DE DADOS (JSON COMPLETO)
// ==============================
let amostraDados;
if (data.length === 0) {
  amostraDados = "Nenhum registro retornado.";
} else {
  amostraDados = JSON.stringify(data, null, 2);
}

// ==============================
// 5. ACESSO AOS BLOCOS DO DIAGN√ìSTICO
// ==============================

// contexto: novo campo principal
const contexto =
  d["üß≠ contexto"] ||
  d["contexto"] ||
  "‚Äî";

// indicadores: agora vem como string no seu prompt novo
const indicadoresRaw = d["üìà indicadores"] || d["indicadores"] || "‚Äî";

let indicadoresDescricao = "‚Äî";
