}

const groupCandidates = campos_texto.filter(f =>
  matchesAny(f, [
    "leiturista",
    "leitor",
    "rota",
    "bairro",
    "categoria",
    "setor",
    "grupo",
    "localidade",
    "regiao",
    "regi√£o",
    "area",
    "√°rea",
    "unidade",
    "uc",
    "cliente"
  ])
);

// limita no m√°ximo 3 campos de agrupamento
const campos_grupo = groupCandidates.slice(0, 3);

// 9) Monta agregados por grupo (limitando quantidade por grupo)
const por_grupo = {};

for (const gf of campos_grupo) {
  const map = {};

  for (const row of sampleData) {
    if (!row || typeof row !== "object" || Array.isArray(row)) continue;

    const rawKey = row[gf];
    const key = rawKey != null ? String(rawKey) : "N/A";

    if (!map[key]) {
      map[key] = { total_registros: 0 };
      for (const f of campos_numericos) {
        map[key][f] = 0;
      }
    }

    map[key].total_registros += 1;
    for (const f of campos_numericos) {
      const n = toNumber(row[f]);
      if (n !== null) {
        map[key][f] += n;
      }
    }
  }

  const arr = Object.entries(map)
    .map(([grupo, stats]) => ({ grupo, ...stats }))
    .sort((a, b) => b.total_registros - a.total_registros)
    .slice(0, 20); // no m√°x. 20 grupos por campo

  por_grupo[gf] = arr;
}

// 10) Top ocorr√™ncias (busca campo candidato de ocorr√™ncia, se existir)
const ocorrField = campos_texto.find(f =>
  matchesAny(f, [
    "ocorrencia",
    "ocorr√™ncia",
    "oco_",
    "cod_ocorr",
    "tipo_ocorr",
    "cod_ocorrencia",
    "descricao_ocorr",
    "descri√ß√£o_ocorr"
  ])
);

let top_ocorrencias = [];

if (ocorrField) {
  const freq = {};

  for (const row of sampleData) {
    if (!row || typeof row !== "object" || Array.isArray(row)) continue;

    const v = row[ocorrField];
    if (v == null) continue;
    const key = String(v);
    freq[key] = (freq[key] ?? 0) + 1;
  }

  top_ocorrencias = Object.entries(freq)
    .map(([valor, qtd]) => ({ valor, qtd }))
    .sort((a, b) => b.qtd - a.qtd)
    .slice(0, 5);
}

// 11) Monta o resumo final, bem compacto
const resumo = {
  dados_validos: true,
  total_registros: data.length,
  campos_numericos,
  campos_texto,
  totais,
  medias,
  campos_grupo,
  por_grupo,
  top_ocorrencias
};

// 12) Retorno para o pr√≥ximo n√≥ (HTTP Request do GPT)
return [{
  json: {
    pedido_cliente,
    resumo
  }
}];
