const items = $input.all();

// --- Utils ------------------------------------------------------------------

function hasMeaningfulContent(o, depth = 0) {
  if (o == null) return false;
  if (typeof o !== 'object') return String(o).trim() !== '';
  if (depth > 6) return true;
  if (Array.isArray(o)) return o.some(v => hasMeaningfulContent(v, depth + 1));
  const keys = Object.keys(o);
  if (keys.length === 0) return false;
  return keys.some(k => hasMeaningfulContent(o[k], depth + 1));
}

function collectErrorsDeep(obj, found = []) {
  if (obj == null) return found;

  const pushErr = (val) => {
    if (val == null) return;
    const msg = typeof val === 'object' ? JSON.stringify(val) : String(val);
    if (msg.trim()) found.push(msg.trim());
  };

  if (Array.isArray(obj)) {
    for (const v of obj) collectErrorsDeep(v, found);
    return found;
  }

  if (typeof obj === 'object') {
    for (const [k, v] of Object.entries(obj)) {
      const lower = k.toLowerCase();
      if (lower === 'erro' || lower === 'error') pushErr(v);
      else if (lower === 'message' && typeof v === 'string') pushErr(v);
      collectErrorsDeep(v, found);
    }
    return found;
  }

  return found;
}

function dedupStrings(arr) {
  const seen = new Set();
  const out = [];
  for (const s of arr) if (!seen.has(s)) { seen.add(s); out.push(s); }
  return out;
}

// PreferÃªncia do Luis: $json.message.content.data
function extractUsefulPayload(j) {
  if (Array.isArray(j?.message?.content?.data)) return j.message.content.data.slice();
  if (Array.isArray(j?.data)) return j.data.slice();
  if (hasMeaningfulContent(j)) return [j];
  return [];
}

function normalizeList(list) {
  return list
    .map(x => (x?.json ?? x))
    .filter(o => hasMeaningfulContent(o))
    .map(o => (typeof o === 'object' ? o : { value: o }));
}

// Captura a query de forma resiliente (suporta Set, Function e mesmo payload)
function safeGetQuery() {
  // 1) Se a query veio nos itens de entrada
  for (const it of items) {
    const j = it?.json ?? {};
    const q = j.query ?? j.sql ?? j.consulta;
    if (typeof q === 'string' && q.trim()) return q.trim();
  }

  // 2) Caso a query esteja em outro nÃ³ do fluxo
  try {
    // Altere o nome abaixo para o nÃ³ onde a SQL Ã© montada (ex: "Extract SQL query" ou "Montar Query")
    const fromNode = $items('Extract SQL query', 0, 0);
    for (const it of fromNode ?? []) {
      const j = it?.json ?? {};
      const q = j.query ?? j.sql ?? j.consulta;
      if (typeof q === 'string' && q.trim()) return q.trim();
    }
  } catch (_) { /* ignora erros */ }

  return null; // nÃ£o encontrada
}

// ----------------------- USAGE (tokens) -------------------------------------

function findUsageIn(obj) {
  if (obj == null) return null;

  if (Array.isArray(obj)) {
    for (const el of obj) {
      const found = findUsageIn(el);
      if (found) return found;
    }
    return null;
  }

  if (typeof obj === 'object') {
    if (obj.usage && typeof obj.usage === 'object') {
      const u = obj.usage;
      const total =
        typeof u.total_tokens === 'number'
          ? u.total_tokens
          : (typeof u.input_tokens === 'number' && typeof u.output_tokens === 'number'
              ? u.input_tokens + u.output_tokens
              : undefined);

      return {
        input_tokens: typeof u.input_tokens === 'number' ? u.input_tokens : undefined,
        output_tokens: typeof u.output_tokens === 'number' ? u.output_tokens : undefined,
        total_tokens: typeof total === 'number' ? total : undefined,
      };
    }

    for (const v of Object.values(obj)) {
      const found = findUsageIn(v);
      if (found) return found;
    }
  }

  return null;
}

function safeGetUsage() {
  // 1) Tenta achar usage nos prÃ³prios itens de entrada
  for (const it of items) {
    const j = it?.json ?? {};
    const usage = findUsageIn(j);
    if (usage) return usage;
  }

  // 2) Tenta achar usage no nÃ³ "GPT gerar query"
  try {
    const fromNode = $items('GPT gerar query'); // <<--- AQUI entra o seu nÃ³
    for (const it of fromNode ?? []) {
      const j = it?.json ?? {};
      const usage = findUsageIn(j);
      if (usage) return usage;
    }
  } catch (_) { /* ignora erros */ }

  return null;
}

