const isOnlySuccess =
  rows.length === 1 &&
  rows[0] &&
  typeof rows[0] === 'object' &&
  Object.keys(rows[0]).length === 1 &&
  Object.prototype.hasOwnProperty.call(rows[0], 'success');

if (rows.length === 0 || isOnlySuccess) {
  return [{ json: { Erro: 'NÃ£o foram encontrados dados para esta solicitaÃ§Ã£o' } }];
}

// 10) OK: retorna SEMPRE { data, query }
