  // === 2. FUNÃ‡ÃƒO DE PARSE COM HEURÃSTICA + NORMALIZAÃ‡ÃƒO ===
  function tryParseWithBraceHeuristic(str) {
    if (typeof str !== "string") return str;

    // 2.1 normalizar escapes comuns gerados pelo modelo (\n, \t)
    str = str.replace(/\\n/g, '\n').replace(/\\t/g, '\t');

    // 2.2 normalizar nÃºmeros com separador de milhar do tipo ": 2,570"
    // Ex.: "total_leituras\": 2,570,"  =>  "total_leituras\": 2570,"
    // PadrÃ£o: ":" + espaÃ§os + atÃ© 3 dÃ­gitos + "," + 3 dÃ­gitos + ("," ou "}")
    str = str.replace(
      /(:\s*)(-?\d{1,3}),(\d{3})(\s*[,\}])/g,
      (_, prefixo, milhar, centena, sufixo) => `${prefixo}${milhar}${centena}${sufixo}`
    );

    // 2.3 tenta JSON.parse direto
    try {
      return JSON.parse(str);
    } catch (e1) {
      // 2.4 heurÃ­stica de balanceamento de chaves
      let depth = 0;
      let inString = false;
      let escape = false;
      let lastCompleteIndex = -1;

      for (let i = 0; i < str.length; i++) {
        const ch = str[i];

        if (escape) {
          escape = false;
          continue;
        }
        if (ch === '\\') {
          escape = true;
          continue;
        }
        if (ch === '"' && !escape) {
          inString = !inString;
          continue;
        }
        if (inString) continue;

        if (ch === '{') {
          depth++;
        } else if (ch === '}') {
          depth--;
          if (depth === 0) {
            lastCompleteIndex = i;
          }
        }
      }

      if (lastCompleteIndex > -1) {
        const candidate = str.slice(0, lastCompleteIndex + 1);
        return JSON.parse(candidate);
      } else {
        throw e1;
      }
    }
  }

  let parsed;
  try {
    parsed = tryParseWithBraceHeuristic(raw);
