function formatMoneyBr(num) {
  if (num == null || isNaN(num)) return "R$ 0,00";
  return (
    "R$ " +
    Number(num).toLocaleString("pt-BR", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    })
  );
}

function formatPercentBr(num) {
  if (num == null || isNaN(num)) return "0%";
  return (
    Number(num).toLocaleString("pt-BR", {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }) + "%"
  );
}

function formatIntegerBr(num) {
  if (num == null || isNaN(num)) return "0";
  return Number(num).toLocaleString("pt-BR", {
    maximumFractionDigits: 0,
  });
}

// decide como formatar de acordo com o nome do campo / contexto pai
function formatNumberWithContext(chave, parentKey, value) {
  const k = (chave || "").toLowerCase();
  const p = (parentKey || "").toLowerCase();

  // 1) VALOR MONETÃRIO
  if (
    k.includes("valor") ||
    k.includes("fatura") ||
    k.includes("faturado") ||
    k.includes("receita") ||
    p.includes("valor_faturado") ||
    p.includes("faturado") ||
    p.includes("faturas")
  ) {
    return formatMoneyBr(value);
  }

  // 2) PERCENTUAL
  if (
    k.includes("percent") ||
    k.includes("perc") ||
    k.endsWith("_pct") ||
    k.endsWith("_percentual") ||
    k.includes("taxa") ||
    k.includes("indice") ||
    k.includes("Ã­ndice")
  ) {
    return formatPercentBr(value);
  }

  // 3) CONSUMO / VOLUME (mÂ³)
  if (
    k.includes("consumo") ||
    k.includes("volume") ||
    k.includes("m3") ||
    k.includes("mÂ³")
  ) {
    return `${formatIntegerBr(value)} mÂ³`;
  }

  // 4) LEITURAS / QUANTIDADES / REGISTROS (inteiro com milhar)
  if (
    k.includes("leitura") ||
    k.includes("leituras") ||
    k.includes("qtd") ||
    k.includes("quantidade") ||
    k.includes("total") ||
    k.includes("economias") ||
    k.includes("registros")
  ) {
    return formatIntegerBr(value);
  }

  // 5) DEFAULT: nÃºmero "cru", mas em pt-BR
  return Number(value).toLocaleString("pt-BR", {
    maximumFractionDigits: 2,
  });
}

// ==============================
