// ==============================
// 6. FORMATADOR GENÃ‰RICO DE "ğŸ”¢ valores"
// ==============================
function formatValores(obj, parentKey = "") {
  if (obj == null) return "â€”";

  // PRIMITIVO
  if (
    typeof obj === "string" ||
    typeof obj === "number" ||
    typeof obj === "boolean"
  ) {
    if (typeof obj === "number") {
      return formatNumberWithContext("", parentKey, obj);
    }
    return String(obj);
  }

  // ARRAY
  if (Array.isArray(obj)) {
    return obj
      .map((item, idx) => {
        if (item && typeof item === "object") {
          const partes = [];
          for (const [k, v] of Object.entries(item)) {
            if (typeof v === "number") {
              partes.push(
                `${k}: ${formatNumberWithContext(k, parentKey, v)}`
              );
            } else {
              partes.push(`${k}: ${toTextSafe(v)}`);
            }
          }
          return `â€¢ item ${idx + 1}: ${partes.join(" | ")}`;
        }
        if (typeof item === "number") {
          return `â€¢ ${formatNumberWithContext("", parentKey, item)}`;
        }
        return `â€¢ ${toTextSafe(item)}`;
      })
      .join("\n");
  }

  // OBJETO
  if (typeof obj === "object") {
    const linhas = [];

    for (const [chave, val] of Object.entries(obj)) {
      const chaveLower = chave.toLowerCase();

      // nÃ£o renderizar anÃ¡lise_interpretativa dentro de valores
      if (
        chaveLower.includes("anÃ¡lise_interpretativa") ||
        chaveLower.includes("analise_interpretativa")
      ) {
        continue;
      }

      // primitivo
      if (
        val == null ||
        typeof val === "string" ||
        typeof val === "number" ||
        typeof val === "boolean"
      ) {
        if (typeof val === "number") {
          linhas.push(
            `- ${chave}: ${formatNumberWithContext(chave, parentKey, val)}`
          );
        } else {
          linhas.push(`- ${chave}: ${toTextSafe(val)}`);
        }
        continue;
      }

      // array
      if (Array.isArray(val)) {
        if (
          val.length > 0 &&
          val.every(
            (item) => item && typeof item === "object" && !Array.isArray(item)
          )
        ) {
          const itensFormatados = val
            .map((item) => {
              const partes = [];

              const categoria =
                item.categoria ||
