                item.tipo ||
                item.Tipo ||
                item.nome ||
                item.Nome;
              const rota = item.rota || item.Rota;
              const bairro = item.bairro || item.Bairro;

              if (rota != null) partes.push(`rota: ${rota}`);
              if (bairro != null) partes.push(`bairro: ${bairro}`);
              if (categoria != null) partes.push(`categoria: ${categoria}`);

              for (const [k2, v2] of Object.entries(item)) {
                if (
                  k2 === "categoria" ||
                  k2 === "Categoria" ||
                  k2 === "tipo" ||
                  k2 === "Tipo" ||
                  k2 === "nome" ||
                  k2 === "Nome" ||
                  k2 === "rota" ||
                  k2 === "Rota" ||
                  k2 === "bairro" ||
                  k2 === "Bairro"
                ) {
                  continue;
                }

                if (typeof v2 === "number") {
                  partes.push(
                    `${k2}: ${formatNumberWithContext(k2, chave, v2)}`
                  );
                } else {
                  partes.push(`${k2}: ${toTextSafe(v2)}`);
                }
              }

              return `  â€¢ ${partes.join(" | ")}`;
            })
            .join("\n");

          linhas.push(`${chave}:\n${itensFormatados}`);
        } else {
          const itens = val
            .map((v) =>
              typeof v === "number"
                ? `  â€¢ ${formatNumberWithContext(chave, parentKey, v)}`
                : `  â€¢ ${toTextSafe(v)}`
            )
            .join("\n");
          linhas.push(`${chave}:\n${itens}`);
        }

        continue;
      }

      // objeto aninhado
      const sub = formatValores(val, chave)
        .split("\n")
        .map((ln) => "  " + ln)
        .join("\n");
      linhas.push(`${chave}:\n${sub}`);
    }

    return linhas.length ? linhas.join("\n") : toTextSafe(obj);
  }

  return toTextSafe(obj);
}

