// 2) Procure erros em todo o payload bruto
let outerErrors = [];
for (const it of items) {
  const j = it?.json ?? {};
  collectErrorsDeep(j, outerErrors);
}
outerErrors = dedupStrings(outerErrors);

// 3) Se achou erro, retorna { Erro, query, errors }
