}

// 6) Checar erros dentro da lista
let innerErrors = [];
collectErrorsDeep(all, innerErrors);
innerErrors = dedupStrings(innerErrors);
if (innerErrors.length > 0) {
  const errors = dedupStrings([...outerErrors, ...innerErrors]);
  return [{ json: { Erro: innerErrors[0], query, errors } }];
}

// 7) Limpeza de dados
const rows = normalizeList(all);

// 8) Sem dados → erro padrão (com query e errors)
if (rows.length === 0) {
