// Entrada: { sql: "SELECT ...", userPrompt: "..." }  (ajuste nomes se preciso)
let sql = ($('Extract SQL query').first().json.query || "").trim();

// 1) Remove comentários (-- e /* */) e normaliza espaços
const stripComments = s => {
  // remove /* ... */ (multilinha)
  s = s.replace(/\/\*[\s\S]*?\*\//g, ' ');
  // remove -- até o fim da linha
  s = s.replace(/--.*$/gm, ' ');
  return s;
};

/**
 * Sanitiza a SQL corrigindo problemas de sintaxe conhecidos
 * (COUNT() vazio e extract(year FROM ...)12 sem "*").
 *
 * A ideia é semelhante ao stripComments: recebe a string e
 * devolve a string sanitizada de uma vez só.
 */
const sanitizeSql = s => {
  let out = s;

  // 1) Corrige COUNT() → COUNT(*)
  //    - pega qualquer COUNT(   ) possivelmente com espaços
  out = out.replace(/COUNT\s*\(\s*\)/gi, 'COUNT(*)');

  // 2) Corrige extract(year FROM algo)12 → extract(year FROM algo)*12
  //    - captura o conteúdo dentro de year FROM ... )
  //    - permite espaços variados
  out = out.replace(
    /extract\s*\(\s*year\s+from\s+([^)]+)\)\s*12/gi,
    'extract(year FROM $1)*12'

  );

  return out;
};

const cleaned = stripComments(sql).replace(/\s+/g, ' ').trim();


const forbidden = /\b(SOMENTE|INSERT|UPDATE|DELETE|MERGE|CREATE|ALTER|DROP|TRUNCATE|GRANT|REVOKE|COMMENT|VACUUM|COPY|REFRESH|CALL|DO|BEGIN|COMMIT|ROLLBACK|SET|LOCK|CLUSTER|REINDEX|DISCARD)\b/i;
if (forbidden.test(cleaned)) {
