// Gera ID e garante que o texto entre com o nome correto
const id = (Date.now().toString(36) + Math.random().toString(36).slice(2, 8)).toUpperCase();

// Tenta achar o texto em v√°rios campos, priorizando 'sqloutput'
const text =
  String(
    $input.first().json.sqloutput??
    ""
  );

return [{
  json: {
    id,
    text,            // <- daqui em diante usamos SEMPRE $json.text
    sqloutput: text  // (opcional) mant√©m tamb√©m em sqloutput pra sua resposta em texto
  }
}];

// ===== ENTRADAS ESPERADAS =====
// $json.id   -> gerado no n√≥ "Gerar ID + passar texto"
// $json.text -> texto com pipes (1¬™ linha = cabe√ßalhos)
// $json.logoUrl (opcional) -> URL do logo a exibir

const id = String($input.first().json.id || "");
const rawText = String($input.first().json.text || "");
const logoUrl = "https://inovamobil.com.br/wp-content/uploads/2023/06/Inovamobil-branco.svg";

// >>> ajuste aqui se sua inst√¢ncia usa base path (ex.: "/n8n")
const BASE_PATH = ""; // ex.: "/n8n"   | deixe "" se n√£o houver base path
const HOST = "https://n8n.srv954202.hstgr.cloud"; // seu dom√≠nio p√∫blico do n8n

// fun√ß√µes utilit√°rias
function escForTemplate(s) {
  return String(s)
    .replace(/\\/g, '\\\\')
    .replace(/`/g, '\\`')
    .replace(/\$\{/g, '\\${');
}
const RAW = escForTemplate(rawText);

const fileName = `visualizador_${id}.html`;
const urlXls = `${HOST}${BASE_PATH}/webhook/visualizar-xls?id=${id}`;

const html = `<!doctype html>
<html lang="pt-br">
<head>
  <meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1">
  <title>Tabela ‚Äî Visualiza√ß√£o + Exportar</title>
  <style>
    :root{ --bg:#0b0e14; --card:#121826; --muted:#a5b1c2; --text:#e6edf3; --border:#273043; --highlight:#1e293b; }
    html, body { margin:0; padding:0; background:var(--bg); color:var(--text); font-family:system-ui,-apple-system,Segoe UI,Roboto,Ubuntu; }
    .wrap { max-width:1200px; margin:24px auto; padding:0 16px; }
    .card { background:var(--card); border:1px solid var(--border); border-radius:16px; padding:16px; box-shadow:0 10px 30px rgba(0,0,0,.25); }
    .header { display:flex; align-items:center; gap:12px; margin-bottom:6px; }
    .logo { width:36px; height:36px; border-radius:8px; object-fit:contain; background:#0f172a; border:1px solid var(--border); display:none; }
    .title { font-size:20px; margin:0; }
    .sub { font-size:12px; color:var(--muted); margin:0 0 12px; }
    .toolbar { display:flex; gap:8px; align-items:center; margin:12px 0 16px; flex-wrap:wrap; }
    .toolbar input[type="text"]{ flex:1; min-width:220px; padding:10px 12px; border-radius:12px; border:1px solid var(--border); background:#0f172a; color:var(--text); outline:none; }
    .btn{ padding:10px 14px; border-radius:12px; border:1px solid var(--border); background:var(--highlight); color:var(--text); cursor:pointer; text-decoration:none; display:inline-block; }
    .btn:hover{ filter:brightness(1.1); }
    .pill { display:inline-block; background:#0f172a; border:1px solid var(--border); border-radius:999px; padding:4px 10px; font-size:12px; color:var(--muted); }
    .table-wrap { max-height:65vh; overflow:auto; border:1px solid var(--border); border-radius:12px; }
    table { width:100%; border-collapse:collapse; }
    thead th { position:sticky; top:0; background:#0f172a; border-bottom:1px solid var(--border); text-align:left; padding:10px; font-weight:600; }
    tbody td { border-bottom:1px solid var(--border); padding:10px; vertical-align:top; }
    tbody tr:hover{ background:rgba(59,130,246,.08); }
    .warn { margin-top:10px; color:#fbbf24; font-size:13px; }
    .footer { display:flex; justify-content:space-between; align-items:center; margin-top:10px; color:var(--muted); font-size:12px; }
  </style>
</head>
<body>
  <div class="wrap">
    <div class="card">
      <div class="header">
        <img id="logo" class="logo" alt="Logo">
        <h1 class="title">Visualiza√ß√£o de Dados</h1>
      </div>
      <p class="sub">Busca r√°pida e exporta√ß√£o via servidor (compat√≠vel com chat/iframe)</p>

      <div class="toolbar">
        <input id="search" type="text" placeholder="Buscar em qualquer coluna‚Ä¶">
        <span class="pill" id="countInfo">0 linhas</span>

        <!-- bot√µes que apontam para endpoints do n8n -->
        <a class="btn" id="exportXls" href="${urlXls}" target="_blank" rel="noopener">Exportar Excel (.xls)</a>
      </div>

      <div class="table-wrap">
        <table id="tbl">
          <thead><tr id="theadRow"></tr></thead>
          <tbody id="tbody"></tbody>
        </table>
      </div>

      <div id="warn" class="warn" style="display:none">‚ö†Ô∏è N√£o encontrei dados ‚Äî verifique se a primeira linha tem cabe√ßalhos separados por ‚Äú|‚Äù.</div>

      <div class="footer">
        <span>O CSV/XLS √© gerado no servidor com base nos dados filtrados originais.</span>
        <span>HTML aut√¥nomo (sem libs).</span>
      </div>
    </div>
  </div>

<script>
// ====== DADOS EMBUTIDOS ======
const RAW_TEXT = \`${RAW}\`;
const LOGO_URL = ${logoUrl ? '`' + escForTemplate(logoUrl) + '`' : '""'};

// ====== PARSER ======
function parsePipeText(raw){
  const lines = raw.split(/\\r?\\n/).map(s => s.trim()).filter(Boolean);
  if (lines.length === 0) return { headers: [], rows: [] };
  const split = s => s.split('|').map(x => x.trim());
  const headers = split(lines[0]);
  const rows = lines.slice(1).map(l => {
    const cols = split(l);
    while (cols.length < headers.length) cols.push("");
    return cols.slice(0, headers.length);
  });
  return { headers, rows };
}
const { headers, rows } = parsePipeText(RAW_TEXT);

// ====== DOM ======
const theadRow = document.getElementById('theadRow');
const tbody = document.getElementById('tbody');
const countInfo = document.getElementById('countInfo');
const warn = document.getElementById('warn');

// ====== LOGO ======
(function setupLogo(){
  if (!LOGO_URL) return;
  const img = document.getElementById('logo');
  img.src = LOGO_URL;
  img.style.display = 'block';
})();

// ====== RENDER ======
function renderTable(hds, rws){
  theadRow.innerHTML = "";
  hds.forEach(h => { const th = document.createElement('th'); th.textContent = h; theadRow.appendChild(th); });
  tbody.innerHTML = "";
  rws.forEach(cols => {
    const tr = document.createElement('tr');
    cols.forEach(c => { const td = document.createElement('td'); td.textContent = c; tr.appendChild(td); });
    tbody.appendChild(tr);
  });
  countInfo.textContent = rws.length + ' linhas';
  warn.style.display = (!hds.length || rws.length === 0) ? '' : 'none';
}

let filtered = rows.slice();
renderTable(headers, filtered);

// ====== BUSCA ======
document.getElementById('search').addEventListener('input', (e)=>{
  const q = e.target.value.toLowerCase();
  filtered = !q ? rows.slice() : rows.filter(cols => cols.some(c => (c||"").toLowerCase().includes(q)));
  renderTable(headers, filtered);
});
</script>
</body>
</html>`;

// ==== IMPORTANTE: al√©m do HTML, vamos salvar tamb√©m o .TXT no servidor ====
// isso √© necess√°rio para os endpoints /visualizar-csv e /visualizar-xls gerarem os arquivos.
// Portanto, este node retorna DOIS bin√°rios: "data" (HTML) e "raw" (TXT).
const base64Html = Buffer.from(html, 'utf8').toString('base64');
const base64Txt  = Buffer.from(rawText, 'utf8').toString('base64');

return [{
  json: {
    fileName,
    txtName: `visualizador_${id}.txt`,
    html,
    text: rawText,
    id
  },
  binary: {
    data: { data: base64Html, fileName, mimeType: 'text/html' },
    raw:  { data: base64Txt,  fileName: `visualizador_${id}.txt`, mimeType: 'text/plain; charset=utf-8' }
  }
}];

