// --- Utils ------------------------------------------------------------------

function hasMeaningfulContent(o, depth = 0) {
  if (o == null) return false;
  if (typeof o !== 'object') return String(o).trim() !== '';
  if (depth > 6) return true;
  if (Array.isArray(o)) return o.some(v => hasMeaningfulContent(v, depth + 1));
  const keys = Object.keys(o);
  if (keys.length === 0) return false;
  return keys.some(k => hasMeaningfulContent(o[k], depth + 1));
}

function collectErrorsDeep(obj, found = []) {
  if (obj == null) return found;

  const pushErr = (val) => {
    if (val == null) return;
    const msg = typeof val === 'object' ? JSON.stringify(val) : String(val);
    if (msg.trim()) found.push(msg.trim());
  };

  if (Array.isArray(obj)) {
    for (const v of obj) collectErrorsDeep(v, found);
    return found;
  }

  if (typeof obj === 'object') {
    for (const [k, v] of Object.entries(obj)) {
      const lower = k.toLowerCase();
      if (lower === 'erro' || lower === 'error') pushErr(v);
      else if (lower === 'message' && typeof v === 'string') pushErr(v);
      collectErrorsDeep(v, found);
    }
    return found;
  }

  return found;
}

function dedupStrings(arr) {
  const seen = new Set();
  const out = [];
  for (const s of arr) if (!seen.has(s)) { seen.add(s); out.push(s); }
  return out;
}

// Prefer√™ncia do Luis: $json.message.content.data
function extractUsefulPayload(j) {
  if (Array.isArray(j?.message?.content?.data)) return j.message.content.data.slice();
  if (Array.isArray(j?.data)) return j.data.slice();
  if (hasMeaningfulContent(j)) return [j];
  return [];
}

function normalizeList(list) {
  return list
    .map(x => (x?.json ?? x))
    .filter(o => hasMeaningfulContent(o))
    .map(o => (typeof o === 'object' ? o : { value: o }));
}

