// ----------------------- USAGE (tokens) -------------------------------------

function findUsageIn(obj) {
  if (obj == null) return null;

  if (Array.isArray(obj)) {
    for (const el of obj) {
      const found = findUsageIn(el);
      if (found) return found;
    }
    return null;
  }

  if (typeof obj === 'object') {
    if (obj.usage && typeof obj.usage === 'object') {
      const u = obj.usage;
      const total =
        typeof u.total_tokens === 'number'
          ? u.total_tokens
          : (typeof u.input_tokens === 'number' && typeof u.output_tokens === 'number'
              ? u.input_tokens + u.output_tokens
              : undefined);

      return {
        input_tokens: typeof u.input_tokens === 'number' ? u.input_tokens : undefined,
        output_tokens: typeof u.output_tokens === 'number' ? u.output_tokens : undefined,
        total_tokens: typeof total === 'number' ? total : undefined,
      };
    }

    for (const v of Object.values(obj)) {
      const found = findUsageIn(v);
      if (found) return found;
    }
  }

  return null;
}

function safeGetUsage() {
