// Run Once for All Items

const items = $input.all();

// --- Utils ------------------------------------------------------------------

function hasMeaningfulContent(o, depth = 0) {
  if (o == null) return false;
  if (typeof o !== 'object') return String(o).trim() !== '';
  if (depth > 6) return true;
  if (Array.isArray(o)) return o.some(v => hasMeaningfulContent(v, depth + 1));
  const keys = Object.keys(o);
  if (keys.length === 0) return false;
  return keys.some(k => hasMeaningfulContent(o[k], depth + 1));
}

function collectErrorsDeep(obj, found = []) {
  if (obj == null) return found;

  const pushErr = (val) => {
    if (val == null) return;
    const msg = typeof val === 'object' ? JSON.stringify(val) : String(val);
    if (msg.trim()) found.push(msg.trim());
  };

  if (Array.isArray(obj)) {
    for (const v of obj) collectErrorsDeep(v, found);
    return found;
  }

  if (typeof obj === 'object') {
    for (const [k, v] of Object.entries(obj)) {
      const lower = k.toLowerCase();
      if (lower === 'erro' || lower === 'error') pushErr(v);
      else if (lower === 'message' && typeof v === 'string') pushErr(v);
      collectErrorsDeep(v, found);
    }
    return found;
  }

  return found;
}

function dedupStrings(arr) {
  const seen = new Set();
  const out = [];
  for (const s of arr) if (!seen.has(s)) { seen.add(s); out.push(s); }
  return out;
}

// Prefer√™ncia do Luis: $json.message.content.data
function extractUsefulPayload(j) {
  if (Array.isArray(j?.message?.content?.data)) return j.message.content.data.slice();
  if (Array.isArray(j?.data)) return j.data.slice();
  if (hasMeaningfulContent(j)) return [j];
  return [];
}

function normalizeList(list) {
  return list
    .map(x => (x?.json ?? x))
    .filter(o => hasMeaningfulContent(o))
    .map(o => (typeof o === 'object' ? o : { value: o }));
}

// Captura a query de forma resiliente (suporta Set, Function e mesmo payload)
function safeGetQuery() {
  // 1) Se a query veio nos itens de entrada
  for (const it of items) {
    const j = it?.json ?? {};
    const q = j.query ?? j.sql ?? j.consulta;
    if (typeof q === 'string' && q.trim()) return q.trim();
  }

  // 2) Caso a query esteja em outro n√≥ do fluxo
  try {
    // Altere o nome abaixo para o n√≥ onde a SQL √© montada (ex: "Extract SQL query" ou "Montar Query")
    const fromNode = $items('Extract SQL query', 0, 0);
    for (const it of fromNode ?? []) {
      const j = it?.json ?? {};
      const q = j.query ?? j.sql ?? j.consulta;
      if (typeof q === 'string' && q.trim()) return q.trim();
    }
  } catch (_) { /* ignora erros */ }

  return null; // n√£o encontrada
}

// --- Fluxo ------------------------------------------------------------------

// 1) Sem entradas
if (!items || items.length === 0) {
  return [{ json: { Erro: 'N√£o foram encontrados dados para esta solicita√ß√£o' } }];
}

// 2) Procure erros em todo o payload bruto
let erros = [];
for (const it of items) {
  const j = it?.json ?? {};
  collectErrorsDeep(j, erros);
}
erros = dedupStrings(erros);

// 3) Se achou erro, retorna somente { Erro }
if (erros.length > 0) {
  return [{ json: { Erro: erros[0] } }];
}

// 4) Extrair dados dos formatos suportados
let all = [];
for (const it of items) {
  const j = it?.json ?? {};
  all.push(...extractUsefulPayload(j));
}

// 5) Fallback: cada item.json
if (all.length === 0) {
  all = items.map(i => i?.json ?? {});
}

// 6) Checar erros dentro da lista
let innerErrors = [];
collectErrorsDeep(all, innerErrors);
innerErrors = dedupStrings(innerErrors);
if (innerErrors.length > 0) {
  return [{ json: { Erro: innerErrors[0] } }];
}

// 7) Limpeza de dados
const rows = normalizeList(all);

// 8) Captura a query (sem quebrar se n√£o existir)
const query = safeGetQuery();

// 9) Sem dados ‚Üí erro padr√£o
if (rows.length === 0) {
  return [{ json: { Erro: 'N√£o foram encontrados dados para esta solicita√ß√£o' } }];
}

// 10) OK: retorna SEMPRE { data, query }
return [{ json: { data: rows, query } }];

// Entrada: { sql: "SELECT ...", userPrompt: "..." }  (ajuste nomes se preciso)
let sql = ($('Extract SQL query').first().json.query || "").trim();

// 1) Remove coment√°rios (-- e /* */) e normaliza espa√ßos
const stripComments = s => {
  // remove /* ... */ (multilinha)
  s = s.replace(/\/\*[\s\S]*?\*\//g, ' ');
  // remove -- at√© o fim da linha
  s = s.replace(/--.*$/gm, ' ');
  return s;
};
const cleaned = stripComments(sql).replace(/\s+/g, ' ').trim();

const forbidden = /\b(SOMENTE|INSERT|UPDATE|DELETE|MERGE|CREATE|ALTER|DROP|TRUNCATE|GRANT|REVOKE|COMMENT|VACUUM|COPY|REFRESH|CALL|DO|BEGIN|COMMIT|ROLLBACK|SET|LOCK|CLUSTER|REINDEX|DISCARD)\b/i;
if (forbidden.test(cleaned)) {
  return [{ 
    allowed: false, 
    reason: "Cont√©m comando proibido.", 
    erro: `Somente SELECT √© permitido. N√£o posso executar DDL/DML.
    Detalhes: ` + $input.first().json.output
  }];
}

return [{ allowed: true, sql }];

