// ===== utils =====
function genId(n) {
  const a = 'ABCDEFGHJKLMNPQRSTUVWXYZ23456789';
  let s = '';
  for (let i = 0; i < (n || 14); i++) s += a[Math.floor(Math.random() * a.length)];
  return s;
}
function esc(s) {
  s = String(s == null ? '' : s);
  return s.replace(/&/g,'&amp;').replace(/</g,'&lt;')
          .replace(/>/g,'&gt;').replace(/"/g,'&quot;');
}
function parsePipe(raw) {
  const lines = String(raw || '')
    .split(/\r?\n/)
    .map(t => t.trim())
    .filter(Boolean);
  if (!lines.length) return { headers: [], rows: [] };

  const split = (s) => s.split('|').map(x => x.trim());
  const headers = split(lines[0]);
  const rows = lines.slice(1).map(l => {
    const cols = split(l);
    while (cols.length < headers.length) cols.push('');
    return cols.slice(0, headers.length);
  });
  return { headers, rows };
}
function buildWorkbookHtml(htmlTable) {
  // Tudo com aspas simples e concatenação p/ evitar crases
  var h = '';
  h += '<html xmlns:o="urn:schemas-microsoft-com:office:office" ';
  h += 'xmlns:x="urn:schemas-microsoft-com:office:excel" ';
  h += 'xmlns="http://www.w3.org/TR/REC-html40">';
  h += '<head><meta charset="utf-8">';
  h += '<!--[if gte mso 9]><xml><x:ExcelWorkbook><x:ExcelWorksheets>';
  h += '<x:ExcelWorksheet><x:Name>Dados</x:Name><x:WorksheetOptions>';
  h += '<x:DisplayGridlines/></x:WorksheetOptions></x:ExcelWorksheet>';
  h += '</x:ExcelWorksheets></x:ExcelWorkbook></xml><![endif]-->';
  h += '</head><body>';
  h += htmlTable;
  h += '</body></html>';
  return h;
}
function toBinary(html, fileName) {
  return [{
    json: {
      ok: true,
      fileName: fileName,
      fileExtension: 'xls',
      mimeType: 'application/vnd.ms-excel'
    },
    binary: {
      data: {
        data: Buffer.from(html, 'utf8').toString('base64'),
        mimeType: 'application/vnd.ms-excel',
        fileName: fileName,
        fileExtension: 'xls'
      }
    }
  }];
}

// ===== entrada do webhook =====
// O Webhook do n8n envia em $json.body
const j = $input.first().json || {};
const body = j.body || j; // garante nos dois formatos

const id = String(body.id || genId(12));
const rawText = body.rawText || body.text || body.sqloutput || '';

// Se não vier rawText, devolve um XLS válido com aviso (p/ o Respond não quebrar)
if (!rawText || !String(rawText).trim()) {
  var tableEmpty = '';
  tableEmpty += '<table border="1">';
  tableEmpty += '<thead><tr><th>Mensagem</th></tr></thead>';
  tableEmpty += '<tbody><tr><td>Sem dados: rawText vazio ou não informado</td></tr></tbody>';
  tableEmpty += '</table>';

  const html = buildWorkbookHtml(tableEmpty);
  return toBinary(html, 'dados_' + id + '_vazio.xls');
}

// Monta a tabela a partir do texto com pipes
const parsed = parsePipe(rawText);
const headers = parsed.headers;
const rows = parsed.rows;

var thead = '<tr>';
for (var i = 0; i < headers.length; i++) {
  thead += '<th>' + esc(headers[i]) + '</th>';
}
thead += '</tr>';

var tbody = '';
for (var r = 0; r < rows.length; r++) {
  var cols = rows[r];
  tbody += '<tr>';
  for (var c = 0; c < cols.length; c++) {
    tbody += '<td>' + esc(cols[c]) + '</td>';
  }
  tbody += '</tr>';
}

var table = '';
table += '<table border="1">';
table += '<thead>' + thead + '</thead>';
table += '<tbody>' + tbody + '</tbody>';
table += '</table>';

// Workbook HTML completo para Excel
const workbookHtml = buildWorkbookHtml(table);

// Retorna binário no campo 'data' (o Respond vai ler esse campo)
return toBinary(workbookHtml, 'dados_' + id + '.xls');

let id = String($input.first().json.query.id || "").trim();
if (!id) return [{ json: { error: "Faltou ?id" } }];

// normaliza entradas: /tmp/..., id puro, visualizador_ID(.html)
id = id.replace(/^\/tmp\//, "");
if (!id.startsWith("visualizador_")) id = `visualizador_${id}`;
if (!id.endsWith(".html")) id = `${id}.html`;

const path = `/tmp/${id}`;
return [{ json: { fileName: id, path } }];
