// MONTAR RESPOSTA PIX/BARRAS ‚Äî code_only (somente o conte√∫do)
// Mode: Run once for each item
try {
  const HANDLER_NODE = "Montar op√ß√µes da fatura selecionada (Em Aberto)";   // ajuste se o nome do seu n√≥ for outro
  const WEBHOOK_NODE = "Receber mensagem";

  const get = (o,p)=>p.split('.').reduce((a,k)=> (a && a[k]!==undefined ? a[k] : null), o || null);
  const unwrap = (v)=>{
    if (Array.isArray(v)) return v;
    if (v && typeof v === 'object') {
      if (Array.isArray(v.data)) return v.data;
      if (Array.isArray(v.body)) return v.body;
      if (Array.isArray(v.rows)) return v.rows;
      return [v];
    }
    return [];
  };
  const findField = (obj, paths)=> {
    for (const p of paths) {
      const val = get(obj, p);
      if (val !== undefined && val !== null && String(val).trim() !== '') return String(val).trim();
    }
    return '';
  };

  const H = $item(0).$node[HANDLER_NODE]?.json || {};
  const W = $item(0).$node[WEBHOOK_NODE]?.json || {};
  const J = $json || {};

  const phone =
    H.phone || J.phone ||
    get(J,'message.content.data.phone') || get(J,'body.phone') ||
    get(W,'body.phone') || null;

  const want =
    H.detailRequest?.want || J.detailRequest?.want || null;   // 'pix' | 'bar'

  const refTarget =
    H.detailRequest?.referencia || J.detailRequest?.referencia || null;

  if (!phone) return { json: { phone: null, message: 'erro: phone ausente' }, pairedItem: [{ item: 0 }] };
  if (!want)  return { json: { phone, message: 'erro: a√ß√£o (pix/bar) ausente' }, pairedItem: [{ item: 0 }] };

  const rows = unwrap(J);
  let row = rows[0] || {};
  if (refTarget) {
    const hit = rows.find(r => (r?.mesAnoReferencia || r?.mes) === refTarget);
    if (hit) row = hit;
  }

  const qr     = findField(row, ['qrCode','qrcode','payload','pix']);
  const linha  = findField(row, ['linhaDigitavel','linha_digitavel','digitableLine','line']);
  const barras = findField(row, ['codigoBarras','codigo_barras','barcode','barCode']);

  if (want === 'pix') {
    if (!qr) return { json: { phone, message: 'PIX indispon√≠vel para esta fatura.' }, pairedItem: [{ item: 0 }] };
    return { json: { phone, message: qr }, pairedItem: [{ item: 0 }] };
  }

  // want === 'bar'
  const text = linha || barras || '';
  if (!text) return { json: { phone, message: 'Linha digit√°vel/c√≥digo indispon√≠vel.' }, pairedItem: [{ item: 0 }] };
  return { json: { phone, message: text }, pairedItem: [{ item: 0 }] };

} catch (e) {
  return { json: { phone: null, message: `erro ao montar resposta: ${String(e?.message || e)}` }, pairedItem: [{ item: 0 }] };
}

