  let base64 = String(raw).trim()
    .replace(/^data:.*?;base64,/i, "")
    .replace(/\s+/g, "");

  // checagem: parece PDF?
  const looksPdf = base64.startsWith("JVBER");
  // bytes estimados: 3/4 do length
  const approxBytes = Math.floor(base64.length * 3 / 4);
  const approxMB = (approxBytes / (1024*1024)).toFixed(2);

